var t = debug.assert;

Array.prototype.reverse = function() {
  var rs = [];
  for(var i = this.length-1; i >= 0; i--) {
    rs.push(this[i]);
  }
  return rs;
};

var a = [1,2,3].reverse();

t( a[0], 3 );
t( a[1], 2 );
t( a[2], 1 );

