function Super() {
  this.run = function() { return 'run'; };
}

function SubClass() {
  this.__proto__ = new Super();

  this.run2 = function() { return 'run2'; };
}

var sub = new SubClass();

t( sub.run != null );
t( sub.run2 != null );

t( sub.run() == 'run' );
t( sub.run2() == 'run2' );

// override
sub.run = function() { return 'override'; };
t( sub.run() == 'override' );
t( sub.run2() == 'run2' );

