test-case.script:
var t = debug.assert;

var catched = false;

try {
  undefinedfunc();

  // The error above should be catched,
  // or we make an assert fail if here can be reached
  t( false );

} catch {

  // catch error
  catched = true;
}

t( catched );

var t = debug.assert;

try {
  if (true) {
    throw new Error('error anyway');
  }

  t( false );
} catch(e) {
  t( typeof e, 'object' );
  t( e instanceof Error );
  t( e.message, 'error anyway' );
}

var t = debug.assert;

var a = 1;

try {
  throw new Error('error anyway');

  t( false );
} catch(e) {
  t( e.message, 'error anyway' );
} finally {

  t( a, 1 );
  a = 0;
}

t( a, 0 );

var t = debug.assert;

try {
  throw 10;

  t( false );
} catch(e) {
  t( e, 10 );
}

var t = debug.assert;

try {
  try {
    throw 'inner error';
  } catch(e) {
    t( e, 'inner error' );

    throw 'outer error';
  }

  t( false );
} catch(e) {
  t( e, 'outer error' );
}
