test-case.script:
var t = debug.assert;

var a = 10, b = 20;

t(a == 10);
t(b == 20);

var t = debug.assert;

var a = 10, b = 20;
var c = b / a, d = a * b;

var e = ( c == d / 100 );

t(e == true);

var t = debug.assert;

var a = true, b = false;

t(a == !b);
t(a != b);

t(!(a < b));
t(!(a > b));

var t = debug.assert;

t( undefined == null );

var a = 10;         // local variable in global scope
                    // is global variable
t( a != null );

a = null;           // clear variable

t( a == null && a == undefined );

var t = debug.assert;

var o1 = {a: 0};

t( o1.a++ == 0 );
t( o1.a == 1 );

var arr = [0,1,2,3];
t( arr[1]++==1 );
t( arr[1]==2 );

var t = debug.assert;

var a = 10;
t( this.a == 10 );  // global variable

function () {
  a = 20;           // access global variable in function
} ();

t( a == 20 );

function () {
  var a = 30;       // access local variable in function
                    // should not to modify global variable
} ();

t( a == 20 );
